function liClear(){for(var e=document.getElementsByClassName("liBtn"),t=0;t<e.length;t++)e[t].classList.remove("liHoverOn")}document.getElementById("sites").onclick=function(){document.getElementById("block-list").style.left="0%",liClear(),this.classList.add("liHoverOn")},document.getElementById("other").onclick=function(){document.getElementById("block-list").style.left="-100%",liClear(),this.classList.add("liHoverOn")};